import { Component, EventEmitter, OnInit, Output } from '@angular/core';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css'],
})
export class NavBarComponent implements OnInit {
  @Output() leagueSelected = new EventEmitter<number>();

  constructor(private readonly router: Router) {}

  countries = [
    { countryName: 'england', id: 39, active: true },
    { countryName: 'spain', id: 140, active: false },
    { countryName: 'germany', id: 78, active: false },
    { countryName: 'france', id: 61, active: false },
    { countryName: 'italy', id: 135, active: false },
  ];

  ngOnInit(): void {
    this.onLeagueSelect(this.countries[0].id);
  }

  onLeagueSelect(id: number) {
    this.countries.map((element) => {
      element.active = false;
      if (element.id === id) {
        element.active = true;
      }
      return element;
    });
    this.leagueSelected.emit(id);
  }
}
