import { Component, OnInit } from '@angular/core';
import { FootballStatsService } from 'src/app/services/football-stats.service';

@Component({
  selector: 'app-standings',
  templateUrl: './standings.component.html',
  styleUrls: ['./standings.component.css'],
})
export class StandingsComponent implements OnInit {
  currentYear = new Date();
  constructor(private readonly footballStatsService: FootballStatsService) {}

  ngOnInit(): void {
    //unsubscribe observable
    console.log(this.currentYear.getFullYear());
    if (localStorage.getItem('39')) {
      this.zipCodeData = JSON.parse(localStorage.getItem('zipcodes') as string);
      this.zipCodeData.push(this.zipCodeInputField.value ?? '');
      localStorage.setItem('zipcodes', JSON.stringify(this.zipCodeData));
    } else {
      //this.footballStatsService
      .getLeagueStandings(39, this.currentYear.getFullYear())
      .subscribe((res) => {
        localStorage.setItem(
          '39',
          JSON.stringify([this.zipCodeInputField.value])
        );
        console.log(res);
      });
      localStorage.setItem(
        '39',
        JSON.stringify([this.zipCodeInputField.value])
      );
    }
    //this.footballStatsService
      .getLeagueStandings(39, this.currentYear.getFullYear())
      .subscribe((res) => {
        //localStorage.setItem(res.response)
        console.log(res);
      });
  }
}
