import { Component, OnDestroy, OnInit } from '@angular/core';
import { FootballStatsService } from '../services/football-stats.service';
import { Subject, takeUntil } from 'rxjs';
import { Standing } from '../models/standing.model';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent implements OnInit, OnDestroy {
  currentYear = new Date();
  currentStandingData = new Array<Standing>();
  private readonly destroy$ = new Subject<void>();

  constructor(private readonly footballStatsService: FootballStatsService) {}

  ngOnInit(): void {
    console.log(this.currentStandingData);
  }

  leagueSelected(id: number) {
    this.getStadingData(id.toString());
  }


  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}
<div class="center">
  <app-nav-bar (leagueSelected)="leagueSelected($event)"></app-nav-bar>
  <app-standings [standingData]="currentStandingData"></app-standings>
</div>
