    <td st (click)="openDetailPage()" [routerLink]="['./details', 123]">
      {{ item.team.name }}
    </td>
    <td>
      {{ item.all.played }}
    </td>
    <td>
      {{ item.all.win }}
    </td>
    <td>
      {{ item.all.lose }}
    </td>
    <td>
      {{ item.all.draw }}
    </td>
    <td>
      {{ item.goalsDiff }}
    </td>
    <td>
      {{ item.points }}
    </td>
  </tr>
</table>
