import { Component, OnDestroy, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Subject, finalize, map, takeUntil, tap } from 'rxjs';
import { Standing } from 'src/app/models/standing.model';
import { FootballStatsService } from 'src/app/services/football-stats.service';

@Component({
  selector: 'app-standings',
  templateUrl: './standings.component.html',
  styleUrls: ['./standings.component.css'],
})
export class StandingsComponent implements OnInit, OnDestroy {
  currentYear = new Date();
  standingData: any;
  private readonly destroy$ = new Subject<void>();
  loading = true;

  constructor(
    private readonly route: ActivatedRoute,
    private readonly footballStatsService: FootballStatsService
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((param) => {
      console.log('sub');
      this.getStadingData(param['id']);
    });
  }

  private getStadingData(id: string) {
    if (localStorage.getItem(id)) {
      this.loading = false;
      this.standingData = JSON.parse(localStorage.getItem(id) as string);
    } else {
      this.loading = true;
      this.footballStatsService
        .getLeagueStandings(+id, this.currentYear.getFullYear())
        .pipe(
          map((res) => {
            console.log('res');
            console.log(res.response[0].league.standings[0]);
            return res.response[0].league.standings[0];
          }),
          tap((res) => {
            localStorage.setItem(id, JSON.stringify(res));
          }),
          takeUntil(this.destroy$)
        )
        .subscribe((res) => {
          this.standingData = res;
          this.loading = false;
        });
    }
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}
<ng-container *ngIf="!loading">
  <table  *ngIf="!loading" style="width: 100%">
    <tr>
      <th></th>
      <th></th>
      <th>Name</th>
      <th>Games</th>
      <th>W</th>
      <th>L</th>
      <th>D</th>
      <th>Goal Difference</th>
      <th>Points</th>
    </tr>
    <tr *ngFor="let item of standingData">
      <td>
        {{ item.rank }}
      </td>
      <td>
        <img
          alt="no image"
          style="width: 20px"
          [src]="item.team.logo"
        />
      </td>
      <td>
        {{ item.team.name }}
      </td>
      <td>
        {{ item.all.played }}
      </td>
      <td>
        {{ item.all.win }}
      </td>
      <td>
        {{ item.all.lose }}
      </td>
      <td>
        {{ item.all.draw }}
      </td>
      <td>
        {{ item.goalsDiff }}
      </td>
      <td>
        {{ item.points }}
      </td>
    </tr>
  </table>
</ng-container>
