import { Component } from '@angular/core';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css'],
})
export class NavBarComponent {
  countries = [
    { countryName: 'england', active: true },
    { countryName: 'spain', active: false },
    { countryName: 'germany', active: false },
    { countryName: 'france', active: false },
    { countryName: 'italy', active: false },
  ];
}
<div><li *ngFor="let item of countries">
  <a [ngClass]="{ active: item.active }" [id]="item.countryName + 'Select'"
    >{{ item.countryName | titlecase }}
  </a>
</li></div>
<li *ngFor="let item of countries">
  <a [ngClass]="{ active: item.active }" [id]="item.countryName + 'Select'"
    >{{ item.countryName | titlecase }}
  </a>
</li>
