import { Component, Input, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Standing } from 'src/app/models/standing.model';

@Component({
  selector: 'app-standings',
  templateUrl: './standings.component.html',
  styleUrls: ['./standings.component.css'],
})
export class StandingsComponent implements OnInit {
  currentStandingData = new Array<Standing>();

  constructor(private readonly route: ActivatedRoute, private readonly footballStatsService: FootballStatsService) {}

  ngOnInit(): void {
    const leagueId = this.route.snapshot.params['id'] as string;
    this.getStadingData(leagueId);
  }

  private getStadingData(id: string) {
    if (localStorage.getItem(id)) {
      console.log('old League: ' + id);
      this.currentStandingData = JSON.parse(
        localStorage.getItem(id) as string
      )[0];
    } else {
      console.log('new League: ' + id);
      this.footballStatsService
        .getLeagueStandings(+id, this.currentYear.getFullYear())
        .pipe(takeUntil(this.destroy$))
        .subscribe((res) => {
          this.currentStandingData = res.response[0].league.standings;
          localStorage.setItem(id, JSON.stringify(this.currentStandingData));
        });
    }
  }
}
