import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class FootballStatsService {
  private apiKey = '7e7df12f91414c05cce3460abdb9b2a2';

  constructor(private readonly http: HttpClient) {}

  /*getLeagueStats(id: number, season: number): Observable<any> {
    const headers = new HttpHeaders({
      'x-rapidapi-host': 'v3.football.api-sports.io',
      'x-rapidapi-key': this.apiKey,
    });
    const p = {
      id: 39,
      name: 'premier league',
      country: 'england',
      code: 'EN',
      season: 2023,
      type: 'league',
    };
    return this.http.get<any>('https://v3.football.api-sports.io/leagues', {
      headers,
      params: {
        id,
        season,
      },
    });
  }*/

  getLeagueStandings(id: number, season: number): Observable<any> {
    const headers = new HttpHeaders({
      'x-rapidapi-host': 'v3.football.api-sports.io',
      'x-rapidapi-key': this.apiKey,
    });
    return this.http.get<any>('https://v3.football.api-sports.io/standings', {
      headers,
      params: {
        id,
        season,
      },
    });
  }

  /*
