import { Component, Input, OnDestroy, OnInit } from '@angular/core';
import { Subject, takeUntil } from 'rxjs';
import { Standing } from 'src/app/models/standing.model';
import { FootballStatsService } from 'src/app/services/football-stats.service';

@Component({
  selector: 'app-standings',
  templateUrl: './standings.component.html',
  styleUrls: ['./standings.component.css'],
})
export class StandingsComponent implements OnInit {
  @Input() standingData: Standing[];

  constructor(private readonly footballStatsService: FootballStatsService) {}

  ngOnInit(): void {}
}
<table style="width: 100%">
  <tr>
    <th></th>
    <th></th>
    <th>Name</th>
    <th>Games</th>
    <th>W</th>
    <th>L</th>
    <th>D</th>
    <th>Goal Difference</th>
    <th>Points</th>
  </tr>
  <tr *ngFor="let item of standingData">
    <td>
      {{ item.rank }}
    </td>
    <td>
      <img alt="no image" style="width: 20px" [src]="item.team.logo" />
    </td>
    <td>
      {{ item.team.name }}
    </td>
    <td>
      {{ item.all.played }}
    </td>
    <td>
      {{ item.all.win }}
    </td>
    <td>
      {{ item.all.lose }}
    </td>
    <td>
      {{ item.all.draw }}
    </td>
    <td>
      {{ item.goalsDiff }}
    </td>
    <td>
      {{ item.points }}
    </td>
  </tr>
</table>
