import { Component, OnDestroy, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent implements OnInit, OnDestroy {

  private readonly destroy$ = new Subject<void>();

  ngOnInit(): void {
    /*
  england: 39
  spain: 140
  germany: 78
  italy: 135
  France: 61
  */
    if (localStorage.getItem('39')) {
      this.standingData = JSON.parse(localStorage.getItem('39') as string)[0];
    } else {
      this.footballStatsService
        .getLeagueStandings(39, this.currentYear.getFullYear())
        .pipe(takeUntil(this.destroy$))
        .subscribe((res) => {
          this.standingData = res.response[0].league.standings;
          localStorage.setItem('39', JSON.stringify(this.standingData));
        });
    }

    console.log(this.standingData);
  }

  leagueSelected($event: number) {
    console.log('$event');
    console.log($event);
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}
<div class="center">
  <app-nav-bar (leagueSelected)="leagueSelected($event)"></app-nav-bar>
  <app-standings></app-standings>
</div>
