import { Component } from '@angular/core';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css'],
})
export class NavBarComponent {
  countries = [
    { countryName: 'england' },
    { countryName: 'spain' },
    { countryName: 'germany' },
    { countryName: 'france' },
    { countryName: 'italy' },
  ];
}
<ul>
  <li *ngFor="let item of countries">
    <a [id]="item.countryName + 'Select'" class="active" href="default.asp">{{
      item.countryName | titlecase
    }}</a>
  </li>
</ul>
